DROP DATABASE IF EXISTS mixer_db;
CREATE DATABASE mixer_db;
\c mixer_db;

CREATE TABLE users (
    id SERIAL PRIMARY KEY,
    username VARCHAR(25) NOT NULL UNIQUE,
    email TEXT NOT NULL UNIQUE,
    password TEXT NOT NULL
);

CREATE TABLE ingredients (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL UNIQUE,
    type VARCHAR(20) NOT NULL,
    img_sm TEXT NOT NULL,
    img_md TEXT NOT NULL,
    img_lg TEXT NOT NULL
);

CREATE TABLE cocktails (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL UNIQUE,
    img TEXT NOT NULL,
    ingredients VARCHAR(255) NOT NULL,
    measurments VARCHAR(255) NOT NULL,
    instructions TEXT NOT NULL,
    likes INT DEFAULT(0)
);

CREATE TABLE fav_cocktails (
    user_id INT NOT NULL REFERENCES users ON DELETE CASCADE,
    cocktail_id INT NOT NULL REFERENCES cocktails ON DELETE CASCADE
);



